/**
 * La clase ImperialLength proporciona información sobre unidades de longitud en el sistema imperial.
 */
export class ImperialLength {
    private readonly pulgadasPorPies = 12;
    private readonly piesPorYardas = 3;
    private readonly yardasPorMillas = 1760;

    /**
     * Crea una instancia de ImperialLength.
     * @param pulgadas La longitud en pulgadas.
     */
    constructor(private pulgadas: number) {}

    /**
     * Devuelve la longitud en pulgadas.
     * @returns La longitud en pulgadas.
     */
    public getPulgadas(): number {
      return this.pulgadas;
    }

    /**
     * Establece la longitud en pulgadas.
     * @param pulgadas La longitud en pulgadas.
     */
    public setPulgadas(pulgadas: number): void {
      this.pulgadas = pulgadas;
    }

    /**
     * Devuelve la longitud en pies.
     * @returns La longitud en pies.
     */
    public getPies(): number {
      return this.pulgadas / this.pulgadasPorPies;
    }

    /**
     * Establece la longitud en pies.
     * @param pies La longitud en pies.
     */
    public setPies(pies: number): void {
      this.pulgadas = pies * this.pulgadasPorPies;
    }

    /**
     * Devuelve la longitud en yardas.
     * @returns La longitud en yardas.
     */
    public getYardas(): number {
      return this.getPies() / this.piesPorYardas;
    }

    /**
     * Establece la longitud en yardas.
     * @param yardas La longitud en yardas.
     */
    public setYardas(yardas: number): void {
      this.pulgadas = yardas * this.piesPorYardas * this.pulgadasPorPies;
    }

    /**
     * Devuelve la longitud en millas.
     * @returns La longitud en millas.
     */
    public getMillas(): number {
      return this.getYardas() / this.yardasPorMillas;
    }

    /**
     * Establece la longitud en millas.
     * @param millas La longitud en millas.
     */
    public setMillas(millas: number): void {
      this.pulgadas = millas * this.yardasPorMillas * this.piesPorYardas * this.pulgadasPorPies;
    }
  }

/**
 * La clase ImperialMass proporciona información sobre unidades de masa en el sistema imperial.
 */
export class ImperialMass {
    private readonly onzasPorLibra = 16;
    private readonly librasPorPiedra = 14;
    private readonly piedrasPorCentenas = 4;
    private readonly centenasPorToneladas = 20;

    /**
     * Crea una instancia de ImperialMass.
     * @param onzas La masa en onzas.
     */
    constructor(private onzas: number) {}

    /**
     * Devuelve la masa en onzas.
     * @returns La masa en onzas.
     */
    public getOnzas(): number {
      return this.onzas;
    }

    /**
     * Establece la masa en onzas.
     * @param onzas La masa en onzas.
     */
    public setOnzas(onzas: number): void {
      this.onzas = onzas;
    }

    /**
     * Devuelve la masa en libras.
     * @returns La masa en libras.
     */
    public getLibras(): number {
      return this.onzas / this.onzasPorLibra;
    }

    /**
     * Establece la masa en libras.
     * @param libras La masa en libras.
     */
    public setLibras(libras: number): void {
      this.onzas = libras * this.onzasPorLibra;
    }

    /**
     * Devuelve la masa en piedras.
     * @returns La masa en piedras.
     */
    public getPiedras(): number {
      return this.getLibras() / this.librasPorPiedra;
    }

    /**
     * Establece la masa en piedras.
     * @param piedras La masa en piedras.
     */
    public setPiedras(piedras: number): void {
      this.onzas = piedras * this.librasPorPiedra * this.onzasPorLibra;
    }

    /**
     * Devuelve la masa en centenas.
     * @returns La masa en centenas.
     */
    public getCentenas(): number {
      return this.getPiedras() / this.piedrasPorCentenas;
    }

    /**
     * Establece la masa en centenas.
     * @param centenas La masa en centenas.
     */
    public setCentenas(centenas: number): void {
      this.onzas = centenas * this.piedrasPorCentenas * this.librasPorPiedra * this.onzasPorLibra;
    }

    /**
     * Devuelve la masa en toneladas.
     * @returns La masa en toneladas.
     */
    public getToneladas(): number {
      return this.getCentenas() / this.centenasPorToneladas;
    }

    /**
     * Establece la masa en toneladas.
     * @param toneladas La masa en toneladas.
     */
    public setTons(toneladas: number): void {
      this.onzas = toneladas * this.centenasPorToneladas * this.piedrasPorCentenas * this.librasPorPiedra * this.onzasPorLibra;
    }
  }

/**
 * Clase para trabajar con diferentes unidades de longitud del sistema métrico
 * @class MetricLength
 */
export class MetricLength {
  /**
   * Convierte una longitud en centímetros a metros
   * @param {number} cm - Longitud en centímetros
   * @returns {number} Longitud en metros
   */
  static cmToM(cm: number): number {
    return cm / 100;
  }

  /**
   * Convierte una longitud en metros a centímetros
   * @param {number} m - Longitud en metros
   * @returns {number} Longitud en centímetros
   */
  static mToCm(m: number): number {
    return m * 100;
  }

  /**
   * Convierte una longitud en metros a kilómetros
   * @param {number} m - Longitud en metros
   * @returns {number} Longitud en kilómetros
   */
  static mToKm(m: number): number {
    return m / 1000;
  }

  /**
   * Convierte una longitud en kilómetros a metros
   * @param {number} km - Longitud en kilómetros
   * @returns {number} Longitud en metros
   */
  static kmToM(km: number): number {
    return km * 1000;
  }
}

/**
 * Clase para trabajar con diferentes unidades de masa del sistema métrico
 * @class MetricMass
 */
export class MetricMass {
  /**
   * Convierte una masa en gramos a kilogramos
   * @param {number} g - Masa en gramos
   * @returns {number} Masa en kilogramos
   */
  static gToKg(g: number): number {
    return g / 1000;
  }

  /**
   * Convierte una masa en kilogramos a gramos
   * @param {number} kg - Masa en kilogramos
   * @returns {number} Masa en gramos
   */
  static kgToG(kg: number): number {
    return kg * 1000;
  }

  /**
   * Convierte una masa en kilogramos a toneladas
   * @param {number} kg - Masa en kilogramos
   * @returns {number} Masa en toneladas
   */
  static kgToT(kg: number): number {
    return kg / 1000;
  }

  /**
   * Convierte una masa en toneladas a kilogramos
   * @param {number} t - Masa en toneladas
   * @returns {number} Masa en kilogramos
   */
  static tToKg(t: number): number {
    return t * 1000;
  }
}
